import React, { useState, useEffect } from 'react';
import {
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  IconButton,
  Typography,
  styled
} from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import BackgroundImage from './images_title/unsplash.jpg'; // Import your background image
import { Link } from 'react-router-dom';

const GalleryHeader = styled('div')(({ theme }) => ({
  display: 'flex',
  alignItems: 'center',
  justifyContent: 'center',
  backgroundImage: `url(${BackgroundImage})`, // Set background image
  backgroundSize: 'cover',
  color: 'white',
  padding: 0,
  position: 'relative',
  height: '30vh',
  width: '100%',
  boxSizing: 'border-box',
  margin: 0,
}));

export default function MyWorkspace() {
  const [images, setImages] = useState([]);

  useEffect(() => {
    fetch('http://127.0.0.1:5000/api/images') // Ensure this URL points to your Flask server
      .then(response => response.json())
      .then(data => setImages(data))
      .catch(error => console.error('Error fetching images:', error));
  }, []);

  const handleDelete = (index) => {
    const newData = images.filter((_, idx) => idx !== index);
    setImages(newData);
  };

  return (
    <div>
      <GalleryHeader>
        <Typography variant="h2">My Workspace</Typography>
      </GalleryHeader>
      <TableContainer component={Paper} sx={{ marginTop: 4 }}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>Image</TableCell>
              <TableCell>Title</TableCell>
              <TableCell>Author</TableCell>
              <TableCell>Actions</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {images.map((item, index) => (
              <TableRow key={index}>
                <TableCell>
                  <img src={`data:image/png;base64,${item.img}`} alt={item.title} width="250" height="150" />
                </TableCell>
                <TableCell>{item.title}</TableCell>
                <TableCell>{item.author}</TableCell>
                <TableCell>
                  <IconButton onClick={() => handleDelete(index)}>
                    <DeleteIcon />
                  </IconButton>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </div>
  );
}
