import React, { useState, useEffect } from 'react';
import {
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  IconButton,
  Typography
} from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import axios from 'axios';  // 引入axios来发起HTTP请求

export default function MyWorkspace() {
  const [images, setImages] = useState([]);

  useEffect(() => {
    fetchImages();
  }, []);

  const fetchImages = () => {
    axios.get('http://127.0.0.1:5000/api/images')
      .then(response => {
        setImages(response.data);
      })
      .catch(error => {
        console.error('Error fetching images:', error);
      });
  };

  const handleDelete = (dbName) => {
    axios.delete(`http://127.0.0.1:5000/api/delete/database/${dbName}`)
      .then(response => {
        console.log(response.data.message);
        fetchImages();  // 刷新图片列表
      })
      .catch(error => {
        console.error('Error deleting database:', error);
      });
  };

  return (
    <div>
      <Typography variant="h4" component="div" sx={{ flexGrow: 1 }}>
        My Workspace
      </Typography>
      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>Image</TableCell>
              <TableCell>Title</TableCell>
              <TableCell>Author</TableCell>
              <TableCell>Actions</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {images.map((item, index) => (
              <TableRow key={index}>
                <TableCell>
                  <img src={`data:image/png;base64,${item.img}`} alt={item.title} width="250" height="150" />
                </TableCell>
                <TableCell>{item.title}</TableCell>
                <TableCell>{item.author}</TableCell>
                <TableCell>
                  <IconButton onClick={() => handleDelete(item.databasename)}>
                    <DeleteIcon />
                  </IconButton>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </div>
  );
}
